import React, { useContext } from 'react'
import { PRODUCTS } from '../../products'
import { ShopContext } from '../../context/shop-context'
import CartItem from './cartItem'
import './cart.css'
const Cart = () => {
  const {cartItems,addItemToCart,removeItemFromCart,handleInputChange}=useContext(ShopContext)
  return (
    <div className='cart'>
      <div><h1>Your Cart Item</h1></div>
      <div className="cart-items">
        {PRODUCTS.map(product=>{
          if(cartItems[product.id]!==0){
            return <CartItem cartItems={cartItems} addItemToCart={addItemToCart} removeItemFromCart={removeItemFromCart} handleInputChange={handleInputChange} data={product}/>
          }
        })}
      </div>

    </div>
  )
}

export default Cart
